import org.scalatest._


class PatternMismatchingSpec extends FlatSpec with Matchers {


  "" should "find all occurrences of mismatched substring" in {
    val pattern = "ATTCTGGA"
    val genome = "CGCCCGAATCCAGAACGCATTCCCATATTTCGGGACCACTGGCCTCCACGGTACGGACGTCAATCAAAT"
    val mismatchCount = 3

    val indexes = new Occurences().allMismatchedOccurences(genome,pattern, mismatchCount)

    assert(indexes.toList === List(6, 7, 26, 27))
  }

  "in test case" should "find all occurrences of mismatched substring" in {
    val pattern = "ACGGAATT"
    val genome = ""
    val mismatchCount = 6

    val indexes = new Occurences().allMismatchedOccurences(genome,pattern, mismatchCount)

    assert(indexes.toList.size === 1)
  }



}


