  def aCycle(adjacency:List[String], initialNode: String):String = {
    val adjacencyMap = adjacency.map((adj) => adj.take(indexOfLeftNode(adj))).zip(adjacency.map((adj) => adj.drop(indexOfRightNode(adj)).split(",").toList)).toMap
    val mutableMap = collection.mutable.Map[String,List[String]](adjacencyMap.toSeq: _*)
    val solution = subCycle(initialNode, mutableMap)
    solution.mkString("->")
  }


  def subCycle(initialNode: String, mutableMap: collection.mutable.Map[String, List[String]]):Traversable[String] = {
    var solution = new scala.collection.mutable.MutableList[String]()
    var startNode: String = initialNode
    solution += startNode
    while (mutableMap.isEmpty == false) {
      val edge: Option[List[String]] = mutableMap.get(startNode)
      if (edge.isDefined) {
        val endNode: String = edge.get.head
        solution += endNode
        if (mutableMap.get(startNode).get.size == 1) {
          mutableMap.remove(startNode)
        } else {
          mutableMap.put(startNode, mutableMap.get(startNode).get.drop(1))
        }
        startNode = endNode
      } else {
        val nextNodeOption = takeFirstFromSolutionWhichStillHasEdge(solution, mutableMap.keySet)
        println("picked node " + nextNodeOption + " to restart with")
        if( nextNodeOption.isDefined) {
          startNode = nextNodeOption.get
          val subsolution = subCycle(startNode,mutableMap)
          val splitted: (collection.mutable.MutableList[String], collection.mutable.MutableList[String]) = solution.splitAt(solution.indexOf(startNode))
          solution = splitted._1 ++ subsolution ++ splitted._2.tail
        } else {
          return solution
        }
      }
//      println(mutableMap)
//      println(solution)
    }
    solution
  }

  def takeFirstFromSolutionWhichStillHasEdge(solution:Traversable[String],mutableMap:scala.collection.Set[String]):Option[String] = {
    solution.find(mutableMap.contains(_))
  }

  def indexOfLeftNode(adj:String):Int = {
    adj.indexOf(" -> ")
  }

  def indexOfRightNode(adj:String) = indexOfLeftNode(adj)+4

